import React from 'react';
import DatePicker from "react-datepicker";
import './Card.scss'
import 'react-datepicker/dist/react-datepicker.css'

class Card extends React.Component{
    
    constructor(){
        super()
        this.state={
            cards:"none",
            Date:new Date()
        }
    }
    handleChange(e){
        console.log("date picked"+e)
        this.setState({
            Date:e
        })

    }

    Carddetect(e){
        console.log("number entered"+e.target.value);
        let v=e.target.value;
        if(v==="1234"){
            this.setState(
                {
                    cards:"block"
                }
            )
        }
    }
    render(){
        
        return(
            <div className="card">
                <div className="inputs">
                    <div className="bar"></div>
                    <div className="input-field">
                    <label>
                        Credit/Debit/ATM card 
                    </label>
                    <input  type="text" placeholder="Enter Card Number" onChange={(e)=>this.Carddetect(e)}/>
                    </div>
                   
                    <div className="datepicker">
                        <label>
                            Valid Thru </label>
                            <DatePicker className="date" onChange={(e)=>this.handleChange(e)} selected={this.state.Date}  />
                             <input className="input"  type="text" placeholder="cvv"/>
                   </div>
                   
        
                </div>
                <div className="cards" style={{display:this.state.cards}}>
                    
                </div>
            </div>)
            
    }
    
}

export default Card;
