import React from "react";
import logo from "./download.jpg";
import "./App.css";
import "./Ninjas";

class App extends React.Component{
    constructor(props){
        super(props);
        this.state={
            newitem:"",
            list:[]
        }
    }

    additem(todov){
        if(todov!==""){
            const newitem={
                id:Date.now(),
                value:todov,
                isdone:false
            };
            const list=[...this.state.list];
            list.push(newitem);
            this.setState({
                list,
                newitem:""
            });
        }
    }


    deleteitem(id){
        const list=[...this.state.list];
        const updatedlist=list.filter(item=>item.id!==id); 
        this.setState({
            list:updatedlist
        })
    }

    updateinput(input){
        this.setState({newitem:input});

    }
    render(){
        return(
            <div className="App">
                                <img src={logo} className="App-logo" alt="image not found"/>
               
                                <h1>Helooo</h1>
                                <Ninjas/>
                    <br/>
                    <input
                    type="text"
                    placeholder="enter action item"
                    value={this.state.newitem}
                    onChange={e=>this.updateinput(e.target.value)}
                    disabled={!this.state.newitem.length}
                    />
                    <button 
                    onClick={()=>this.additem(this.state.newitem)}>Add action</button>
                    <div className="action">
                        <ul>
                            <li >
                                <input type="checkbox" name="" id=""/>
                               learn react
                                <button>delete</button>

                            </li>
                        </ul>
                    </div>
                </div>
            
        );
    }
}
export default App;
