import React from 'react';
import logo from './download.jpg';
import "./App.css";
import Recipe from "./Recipe";

const App = () =>{

const APP_ID='ace22d99';
const APP_KEY='255ebfc6cefdf7b0644be2c2c7cf16ef	';

const [recipes,setRecipes]=React.useState([]);
React.useEffect(() =>{
    getRecipes();  
},[]);

const getRecipes=async () =>{
    const resp=await fetch('https://api.edamam.com/search?q=chicken&app_id=ace22d99&app_key=255ebfc6cefdf7b0644be2c2c7cf16ef');
        const data= await resp.json();
        console.log(data.hits);
        setRecipes(data.hits);
}



return(
<div className="App">
    
    <form>
        <input type="text"/>
        <button type="submit" >     Search   </button>
    </form>
    {
        recipes.map(recipe=>(
         <Recipe/>   
        ))
    }
</div>
);

}
export default App;
