import React from 'react';
import logo from './download.jpg';
import "./App.css";
import Recipe from "./Recipe";

const App = () =>{

const APP_ID='ace22d99';
const APP_KEY='255ebfc6cefdf7b0644be2c2c7cf16ef	';

const [recipes,setRecipes]=React.useState([]);

const [search ,setSearch]=React.useState("");

const[query,setQuery]=React.useState('chicken');

React.useEffect(() =>{
    getRecipes();  
},[query]);

const getRecipes=async () =>{
    const resp=await fetch(`https://api.edamam.com/search?q=${query}&app_id=${APP_ID}&app_key=${APP_KEY}`);
        const data= await resp.json();
        console.log(data.hits);
        setRecipes(data.hits);
}

const Updatesearch=e=>{
    setSearch(e.target.value);
        console.log(search);
}

const Searchbar=e=>{
    e.preventDefault();
    setQuery(search);
}

return(
<div className="App">
    
        <form onSubmit={Searchbar}>
        <input type="text" value={search} onChange={Updatesearch} />
        <button type="submit"  >     Search   </button>
    </form>
    {
        recipes.map(recipe=>(
         <Recipe 
         key={recipe.recipe.label}
         title={recipe.recipe.label}
          calories={recipe.recipe.calories}
          image={recipe.recipe.image}
         />   
        ))
    }
</div>
);

}
export default App;
